    def buttonChercher(self):
        global Code
        Code=self.Code.text()
        if (Mat.SaisieCode(Code))and (Code in Mat.Matieres.keys()) :
            self.CodeObjects (True)   
            self.CoefficientObjects (False)
            self.Matiereinexistante.setHidden(True)  
            self.infoinvalideCode.setHidden(True)     
        elif not(Mat.SaisieCode(Code)):
            self.infoinvalideCode.setHidden(False)     
            self.Matiereinexistante.setHidden(True)
        elif not(Code  in Mat.Matieres.keys()):
            self.Matiereinexistante.setHidden(False) 
            self.infoinvalideCode.setHidden(True)
      

    
    def buttonModifier(self):
        Coefficient=self.Coefficient.text()
        if (Mat.SaisieCoefficient(Coefficient)) :
            Mat.Matieres[Code][0]=Coefficient  
            self.infoinvalideCoefficient.setHidden(True)     
        else:
            self.infoinvalideCoefficient.setHidden(False)
 

