    def getInfos(self):
        Semestre=(self.Semestre.text()).upper()
        Section=self.Section.text()
        dictSec={}
        dictSem={}
        print (Section)
        if (Mat.SaisieSemestre(Semestre)and Mat.SaisieSection(Section)) :
            if (Mat.RechercheParSemestre(Mat.Matieres,Semestre) and Mat.RechercheParSection(Mat.Matieres,Section) ): 
                TabSec=KeysSec(Section)
                TabSem=KeysSem(Semestre)
                dictSec.update ({i:Mat.Matieres[i] for i in TabSec})
                dictSem.update ({i:Mat.Matieres[i] for i in TabSem})
                print (dictSem)
                print (dictSec)
                Dict = {x:dictSec[x] for x in dictSec 
                              if x in dictSem}
                
                self.openAffichageMatiere(Dict)
                self.Matiereinexistante.setHidden(True)
                self.infoinvalideSemestre.setHidden(True)
                self.infoinvalideSection.setHidden(True)
                
            else :
                self.Matiereinexistante.setHidden(False)
                self.infoinvalideSemestre.setHidden(True)
                self.infoinvalideSection.setHidden(True)
        if not (Mat.SaisieSemestre(Semestre)):
            self.infoinvalideSemestre.setHidden(False)
            
        else:
            self.infoinvalideSemestre.setHidden(True)
        
        
        if not (Mat.SaisieSection(Section)):
            self.infoinvalideSection.setHidden(False)
            
        else:
            self.infoinvalideSection.setHidden(True)
        
                
