    def getInfos(self):
        Niveau=self.Niveau.text()
        T=[]

        if (ET.SaisieNiveauEtude(Niveau)) :
            if (ET.RechercheParNiveau(ET.Etudiants,Niveau)): 
                Tab=Keys(Niveau)
                T = []
                for i in list(NT.Notes.keys()):

                    if i[0] in Tab:
                        T.append(i)

                for j in Tab:
                    ET.Etudiants.pop(j)
                for i in T:
                    NT.Notes.pop(i)
                self.Niveaunexistepas.setHidden(True)
                self.SuppressionErreur.setHidden(True)
                self.infoinvalide.setHidden(True)     
            else :
                self.Niveaunexistepas.setHidden(False)
                self.SuppressionErreur.setHidden(False)
                self.infoinvalide.setHidden(True)
        else:
            self.infoinvalide.setHidden(False)
            self.SuppressionErreur.setHidden(False)
            self.Niveaunexistepas.setHidden(True)

