    def getInfos(self):
        Section=self.Section.text()

        if (ET.SaisieSection(Section)) :
            if (ET.RechercheParSection(ET.Etudiants,Section)):
                Tab=Keys(Section)
                T = []
                for i in list(NT.Notes.keys()):

                    if i[0] in Tab:
                        T.append(i)
                for j in Tab:
                    ET.Etudiants.pop(j)
                for i in T:
                    NT.Notes.pop(i)

                self.Sectionnexistepas.setHidden(True)
                self.SuppressionErreur.setHidden(True)
                self.infoinvalide.setHidden(True)
            else :
                self.Sectionnexistepas.setHidden(False)
                self.SuppressionErreur.setHidden(False)
                self.infoinvalide.setHidden(True)
        else:
            self.infoinvalide.setHidden(False)
            self.SuppressionErreur.setHidden(False)
            self.Sectionnexistepas.setHidden(True)

