    def getInfos(self):
        Numinscrit=self.Numinscrit.text()
        Nom=self.Nom.text()
        Prenom=self.Prenom.text()
        DatedeNaissance=self.DatedeNaissance.text()
        Adresse=self.Adresse.text() 
        Mail=self.Mail.text()
        Telephone=self.Telephone.text()
        Section=self.Section.text()
        NiveauEtude=self.NiveauEtude.text()
        
        if (ET.SaisieNumInscrit(Numinscrit) and (Numinscrit not in ET.Etudiants.keys())   and ET.SaisieNom(Nom) and ET.SaisieNom(Prenom) and ET.SaisieDate(DatedeNaissance) and ET.SaisieAdresse(Adresse) and ET.SaisieMail(Mail)and ET.SaisieTelephone(Telephone) and ET.SaisieSection(Section) and ET.SaisieNiveauEtude(NiveauEtude)):  
            ET.Etudiants.update({Numinscrit:[Nom,Prenom,DatedeNaissance,Adresse,Mail,Telephone,Section,NiveauEtude]})
            self.AjoutInvalide.setHidden(True)
        else :
            self.AjoutInvalide.setHidden(False)
